import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Dialog, DialogActions, DialogContent, DialogTitle, TextField, Button,
  Table, TableBody, TableCell, TableHead, TableRow, IconButton
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const Signup = () => {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [formData, setFormData] = useState({ SNo: '', firstName: '', lastName: '', email: '', password: '' });
  const [dialogOpen, setDialogOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Fetch users from the API
    axios.get('http://localhost:5000/api/users')
      .then(response => setUsers(response.data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleDialogOpen = (user = null) => {
    if (user) {
      setSelectedUser(user);
      setFormData({ SNo: user.SNo, firstName: user.firstName, lastName: user.lastName, email: user.email, password: '' });
      setIsEditing(true);
    } else {
      setFormData({ SNo: '', firstName: '', lastName: '', email: '', password: '' });
      setIsEditing(false);
    }
    setDialogOpen(true);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
    setSelectedUser(null);
  };

  const handleSubmit = () => {
    if (isEditing) {
      axios.put(`http://localhost:5000/api/users/${selectedUser._id}`, formData)
        .then(response => {
          const updatedUser = response.data;
          setUsers(users.map(user => (user._id === updatedUser._id ? updatedUser : user)));
          handleDialogClose();
        })
        .catch(error => console.error('Error updating user:', error));
    } else {
      axios.post('http://localhost:5000/api/users', formData)
        .then(response => {
          setUsers([...users, response.data]);
          handleDialogClose();
        })
        .catch(error => console.error('Error creating user:', error));
    }
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/users/${id}`)
      .then(() => setUsers(users.filter(user => user._id !== id)))
      .catch(error => console.error('Error deleting user:', error));
  };

  return (
    <div>
      <Button variant="contained" color="primary" onClick={() => handleDialogOpen()}>
        Add User
      </Button>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>SNo</TableCell>
            <TableCell>First Name</TableCell>
            <TableCell>Last Name</TableCell>
            <TableCell>Email</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {users.map(user => (
            <TableRow key={user._id}>
              <TableCell>{user.SNo}</TableCell>
              <TableCell>{user.firstName}</TableCell>
              <TableCell>{user.lastName}</TableCell>
              <TableCell>{user.email}</TableCell>
              <TableCell>
                <IconButton onClick={() => handleDialogOpen(user)}><EditIcon /></IconButton>
                <IconButton onClick={() => handleDelete(user._id)}><DeleteIcon /></IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      <Dialog open={dialogOpen} onClose={handleDialogClose}>
        <DialogTitle>{isEditing ? 'Edit User' : 'Add User'}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            name="SNo"
            label="SNo"
            value={formData.SNo}
            onChange={handleInputChange}
            fullWidth
          />
          <TextField
            margin="dense"
            name="firstName"
            label="First Name"
            value={formData.firstName}
            onChange={handleInputChange}
            fullWidth
          />
          <TextField
            margin="dense"
            name="lastName"
            label="Last Name"
            value={formData.lastName}
            onChange={handleInputChange}
            fullWidth
          />
          <TextField
            margin="dense"
            name="email"
            label="Email"
            value={formData.email}
            onChange={handleInputChange}
            fullWidth
          />
          <TextField
            margin="dense"
            name="password"
            label="Password"
            type="password"
            value={formData.password}
            onChange={handleInputChange}
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleSubmit} color="primary">
            {isEditing ? 'Update' : 'Add'}
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default Signup;
