import React, { useState, useEffect } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, TextField, Button } from '@mui/material';
import axios from 'axios';
const Signup = ({ open, onClose, editUserId, fetchUsers, setEditUserId }) => {
    const [formData, setFormData] = useState({ firstName: '', lastName: '', email: '', password: '' });

    useEffect(() => {
      if (editUserId) {
        const fetchUser = async () => {
          const response = await axios.get(`http://localhost:3001/api/signup/${editUserId}`);
          setFormData(response.data);
        };
        fetchUser();
      } else {
        setFormData({ firstName: '', lastName: '', email: '', password: '' });
      }
    }, [editUserId]);
  
    const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    };
  
    const handleSubmit = async () => {
      if (editUserId) {
        await axios.put(`http://localhost:3001/api/signup/${editUserId}`, formData);
        setEditUserId(null);
      } else {
        await axios.post('http://localhost:3001/api/signup', formData);
      }
      setFormData({ firstName: '', lastName: '', email: '', password: '' });
      fetchUsers();
      onClose();
    };
  
    return (
      <Dialog open={open} onClose={onClose}>
        <DialogTitle>{editUserId ? 'Update User' : 'Add User'}</DialogTitle>
        <DialogContent>
          <TextField
            label="First Name"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            required
            margin="normal"
            fullWidth
          />
          <TextField
            label="Last Name"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            required
            margin="normal"
            fullWidth
          />
          <TextField
            label="Email"
            name="email"
            type="email"
            value={formData.email}
            onChange={handleChange}
            required
            margin="normal"
            fullWidth
          />
          <TextField
            label="Password"
            name="password"
            type="password"
            value={formData.password}
            onChange={handleChange}
            required
            margin="normal"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={onClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleSubmit} color="primary">
            {editUserId ? 'Update' : 'Add'}
          </Button>
        </DialogActions>
      </Dialog>
    );
  };
  

export default Signup
