import './App.css';
import React, { useState, useEffect } from 'react';
import DataTable from './DataTable';
import Signup from './Signup';
import axios from 'axios';

function App() {
  const [users, setUsers] = useState([]);
  const [editUserId, setEditUserId] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await axios.get('http://localhost:3001/api/signup');
    setUsers(response.data);
  };
  return (
    <div>
    <Signup editUserId={editUserId} fetchUsers={fetchUsers} setEditUserId={setEditUserId} />
    <DataTable users={users} setEditUserId={setEditUserId} fetchUsers={fetchUsers} />
  </div>
  );
}

export default App;
