import React, { useEffect, useState } from 'react';
import { Table, TextField, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const DataTable = () => {
  const [data, setData] = useState([]);
  const [editItem, setEditItem] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [openConfirmDialog, setOpenConfirmDialog] = useState(false); // State for confirmation dialog
  const [itemToDelete, setItemToDelete] = useState(null); // State for the item to delete
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
      fetchData();
  }, []);

  const fetchData = () => {
      axios.get('http://localhost:3001/api/signup')
          .then(response => {
              setData(response.data);
          })
          .catch(error => {
              console.error('There was an error fetching the data!', error);
          });
  };

  const handleDeleteClick = (item) => {
      setItemToDelete(item);
      setOpenConfirmDialog(true); // Open the confirmation dialog
  };

  const handleDeleteConfirm = () => {
      axios.delete(`http://localhost:3001/api/signup/${itemToDelete._id}`)
          .then(() => {
              setOpenConfirmDialog(false);
              fetchData();
          })
          .catch(error => {
              console.error('There was an error deleting the item!', error);
          });
  };

  const handleEditClick = (item) => {
      setEditItem(item);
      setFirstName(item.firstName);
      setLastName(item.lastName);
      setEmail(item.email);
      setPassword(item.password);
      setOpenDialog(true);
  };

  const handleUpdate = () => {
      axios.put(`http://localhost:3001/api/signup/${editItem._id}`, {
          firstName,
          lastName,
          email,
          password
      })
          .then(() => {
              setOpenDialog(false);
              fetchData();
          })
          .catch(error => {
              console.error('There was an error updating the item!', error);
          });
  };

  return (
      <div>
          <Button
              variant="contained"
              color="primary"
              onClick={() => navigate('/signup')}
              sx={{ mb: 2 }}
          >
              Add User
          </Button>
          <TableContainer component={Paper}>
              <Table>
                  <TableHead>
                      <TableRow>
                          <TableCell>First Name</TableCell>
                          <TableCell>Last Name</TableCell>
                          <TableCell>Email</TableCell>
                          <TableCell>Password</TableCell>
                          <TableCell>Actions</TableCell>
                      </TableRow>
                  </TableHead>
                  <TableBody>
                      {data.map((item) => (
                          <TableRow key={item._id}>
                              <TableCell>{item.firstName}</TableCell>
                              <TableCell>{item.lastName}</TableCell>
                              <TableCell>{item.email}</TableCell>
                              <TableCell>{item.password}</TableCell>
                              <TableCell>
                                  <Button onClick={() => handleEditClick(item)} color="primary">Edit</Button>
                                  <Button onClick={() => handleDeleteClick(item)} color="secondary">Delete</Button>
                              </TableCell>
                          </TableRow>
                      ))}
                  </TableBody>
              </Table>
          </TableContainer>

          {/* Edit Dialog */}
          <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
              <DialogTitle>Edit Item</DialogTitle>
              <DialogContent>
                  <TextField
                      label="First Name"
                      variant="outlined"
                      fullWidth
                      margin="normal"
                      value={firstName}
                      onChange={(e) => setFirstName(e.target.value)}
                      required
                  />
                  <TextField
                      label="Last Name"
                      variant="outlined"
                      fullWidth
                      margin="normal"
                      value={lastName}
                      onChange={(e) => setLastName(e.target.value)}
                      required
                  />
                  <TextField
                      label="Email"
                      type="email"
                      variant="outlined"
                      fullWidth
                      margin="normal"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      required
                  />
                  <TextField
                      label="Password"
                      type="password"
                      variant="outlined"
                      fullWidth
                      margin="normal"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                  />
              </DialogContent>
              <DialogActions>
                  <Button onClick={() => setOpenDialog(false)} color="primary">Cancel</Button>
                  <Button onClick={handleUpdate} color="primary">Update</Button>
              </DialogActions>
          </Dialog>

          {/* Confirmation Dialog */}
          <Dialog open={openConfirmDialog} onClose={() => setOpenConfirmDialog(false)}>
              <DialogTitle>Confirm Deletion</DialogTitle>
              <DialogContent>
                  Are you sure you want to delete this user?
              </DialogContent>
              <DialogActions>
                  <Button onClick={() => setOpenConfirmDialog(false)} color="primary">Cancel</Button>
                  <Button onClick={handleDeleteConfirm} color="secondary">Delete</Button>
              </DialogActions>
          </Dialog>
      </div>
  );
};

export default DataTable;
