import React, { useState, useEffect } from 'react';
import { Dialog, DialogActions, DialogContent, DialogTitle, TextField, Button } from '@mui/material';
import axios from 'axios';
const Signup = ({ open, onClose, editUserId, fetchUsers, setEditUserId }) => {
    const [formData, setFormData] = useState({ firstName: '', lastName: '', email: '', password: '' });
    const [errors, setErrors] = useState({});

    useEffect(() => {
        if (editUserId) {
            const fetchUser = async () => {
                const response = await axios.get(`http://localhost:3001/api/signup/${editUserId}`);
                setFormData(response.data);
            };
            fetchUser();
        } else {
            setFormData({ firstName: '', lastName: '', email: '', password: '' });
        }
    }, [editUserId]);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const validate = () => {
        let tempErrors = {};

        // Validate First Name
        tempErrors.firstName = formData.firstName ? "" : "First Name is required.";

        // Validate Last Name
        tempErrors.lastName = formData.lastName ? "" : "Last Name is required.";

        // Password validation
        tempErrors.password = formData.password.length >= 8
            ? ""
            : "Password must be at least 8 characters long.";

        setErrors(tempErrors);

        // Since email validation is handled by the browser, we only check other fields
        return Object.values(tempErrors).every(x => x === "") && formData.email !== "";
    };

    const handleSubmit = async () => {
        if (validate()) {
            if (editUserId) {
                await axios.put(`http://localhost:3001/api/signup/${editUserId}`, formData);
                setEditUserId(null);
            } else {
                await axios.post('http://localhost:3001/api/signup', formData);
            }
            setFormData({ firstName: '', lastName: '', email: '', password: '' });
            fetchUsers();
            onClose();
        }
    };

    return (
        <Dialog open={open} onClose={onClose}>
            <DialogTitle>{editUserId ? 'Update User' : 'Add User'}</DialogTitle>
            <DialogContent>
                <TextField
                    label="First Name"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                    margin="normal"
                    fullWidth
                    error={!!errors.firstName}
                    helperText={errors.firstName}
                />
                <TextField
                    label="Last Name"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                    margin="normal"
                    fullWidth
                    error={!!errors.lastName}
                    helperText={errors.lastName}
                />
                <TextField
                    label="Email"
                    name="email"
                    type="email" // Leverages browser's native email validation
                    value={formData.email}
                    onChange={handleChange}
                    required
                    margin="normal"
                    fullWidth
                    error={!!errors.email} // Show error state if needed
                    helperText={errors.email} // Display validation message
                />
                <TextField
                    label="Password"
                    name="password"
                    type="password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    margin="normal"
                    fullWidth
                    error={!!errors.password}
                    helperText={errors.password}
                />
            </DialogContent>
            <DialogActions>
                <Button onClick={onClose} color="secondary">
                    Cancel
                </Button>
                <Button onClick={handleSubmit} color="primary">
                    {editUserId ? 'Update' : 'Add'}
                </Button>
            </DialogActions>
        </Dialog>
    );
};


export default Signup
