import React, { useEffect, useState } from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, TextField, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';
import axios from 'axios';
const DataTable = () => {
  const [data, setData] = useState([]);
  const [editItem, setEditItem] = useState(null);
  const [openDialog, setOpenDialog] = useState(false);
  const [newUsername, setNewUsername] = useState('');
  const [newEmail, setNewEmail] = useState('');

  useEffect(() => {
      fetchData();
  }, []);

  const fetchData = () => {
      axios.get('http://localhost:3001/api/signup')
          .then(response => {
              setData(response.data);
          })
          .catch(error => {
              console.error('There was an error fetching the data!', error);
          });
  };

  const handleDelete = (id) => {
      axios.delete(`http://localhost:3001/api/signup/${id}`)
          .then(() => {
              fetchData();
          })
          .catch(error => {
              console.error('There was an error deleting the item!', error);
          });
  };

  const handleEditClick = (item) => {
      setEditItem(item);
      setNewUsername(item.username);
      setNewEmail(item.email);
      setOpenDialog(true);
  };

  const handleUpdate = () => {
      axios.put(`http://localhost:3001/api/signup/${editItem._id}`, {
          username: newUsername,
          email: newEmail
      })
      .then(() => {
          setOpenDialog(false);
          fetchData();
      })
      .catch(error => {
          console.error('There was an error updating the item!', error);
      });
  };

  return (
      <div>
          <TableContainer component={Paper}>
              <Table>
                  <TableHead>
                      <TableRow>
                          <TableCell>Username</TableCell>
                          <TableCell>Email</TableCell>
                          <TableCell>Actions</TableCell>
                      </TableRow>
                  </TableHead>
                  <TableBody>
                      {data.map((item) => (
                          <TableRow key={item._id}>
                              <TableCell>{item.username}</TableCell>
                              <TableCell>{item.email}</TableCell>
                              <TableCell>
                                  <Button onClick={() => handleEditClick(item)} color="primary">Edit</Button>
                                  <Button onClick={() => handleDelete(item._id)} color="secondary">Delete</Button>
                              </TableCell>
                          </TableRow>
                      ))}
                  </TableBody>
              </Table>
          </TableContainer>

          <Dialog open={openDialog} onClose={() => setOpenDialog(false)}>
              <DialogTitle>Edit Item</DialogTitle>
              <DialogContent>
                  <TextField
                      autoFocus
                      margin="dense"
                      label="Username"
                      type="text"
                      fullWidth
                      variant="outlined"
                      value={newUsername}
                      onChange={(e) => setNewUsername(e.target.value)}
                  />
                  <TextField
                      margin="dense"
                      label="Email"
                      type="email"
                      fullWidth
                      variant="outlined"
                      value={newEmail}
                      onChange={(e) => setNewEmail(e.target.value)}
                  />
              </DialogContent>
              <DialogActions>
                  <Button onClick={() => setOpenDialog(false)} color="primary">Cancel</Button>
                  <Button onClick={handleUpdate} color="primary">Update</Button>
              </DialogActions>
          </Dialog>
      </div>
  );
};

export default DataTable;
