import React, { useState } from 'react';
import { TextField, Button, Box, Typography, Snackbar, Alert } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Authentication = ({ authMode }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(''); // State to hold error messages
    const [openSnackbar, setOpenSnackbar] = useState(false); // State to manage the snackbar visibility
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const apiEndpoint = authMode === 'login' 
                ? 'http://localhost:3001/api/login' 
                : 'http://localhost:3001/api/signup';
            
            await axios.post(apiEndpoint, { email, password });

            // Clear form fields after successful submission
            setEmail('');
            setPassword('');

            // Redirect to the dashboard or any other page after successful authentication
            navigate('/dashboard');
        } catch (error) {
            console.log(`Error during ${authMode}:`, error.response?.data?.message || error.message);
            setError(error.response?.data?.message || 'An error occurred'); // Set the error message
            setOpenSnackbar(true); // Show the snackbar
        }
    };

    const handleCloseSnackbar = () => {
        setOpenSnackbar(false); // Close the snackbar
    };

    return (
        <Box
            display="flex"
            flexDirection="column"
            alignItems="center"
            justifyContent="center"
            height="100vh"
            bgcolor="#f5f5f5"
        >
            <Box
                component="form"
                onSubmit={handleSubmit}
                sx={{
                    p: 4,
                    bgcolor: 'white',
                    boxShadow: 3,
                    borderRadius: 2,
                    minWidth: 300,
                }}
            >
                <Typography variant="h5" gutterBottom textAlign="center">
                    {authMode === 'login' ? 'Login' : 'Sign Up'}
                </Typography>
                <TextField
                    label="Email"
                    type="email"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
                <TextField
                    label="Password"
                    type="password"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    fullWidth
                    sx={{ mt: 2 }}
                >
                    {authMode === 'login' ? 'Login' : 'Sign Up'}
                </Button>
            </Box>

            {/* Snackbar to display error messages */}
            <Snackbar
                open={openSnackbar}
                autoHideDuration={6000}
                onClose={handleCloseSnackbar}
                anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
            >
                <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
                    {error}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default Authentication;
